ee:message.ee:set-payload:
%dw 2.0
output application/xml
---
{
	IDoc: {
		E1MARAM: {
			MATNR: payload.materialId,
			MAKTX: payload.name,
			MTART: "CAR",
			MATKL: payload.category,
			HERST: payload.manufacturer,
			BISMT: payload.year,
			NETPR: payload.price,
			BSTMG: payload.stock
		}
	}
}

%dw 2.0
output application/xml
---
{
	IDoc: {
		 E1MARAM: payload map(item) -> {
		    MATNR: item.materialId,
			MAKTX: item.name,
			MTART: "CAR",
			MATKL: item.category,
			HERST: item.manufacturer,
			BISMT: item.year,
			NETPR: item.price,
			BSTMG: item.stock
		}
	}
}
